function replaceTextInElementById(elementId,text){document.getElementById(elementId).innerHTML=text}function updateHitCounterText(url,elementId,count=!1){const hitCountDomain="rdyx",countUrl=count?"count":"nocount",hitCountUrl="https://rdyx.pythonanywhere.com/"+countUrl+"?url="+url,xhr=new XMLHttpRequest;xhr.withCredentials=!0,xhr.open("GET",hitCountUrl),xhr.onload=function(){if(200===xhr.status){const hitCount=xhr.responseText;replaceTextInElementById(elementId,hitCount)}},xhr.send()}